import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { client,alert } from 'src/app/interfaces/interfaces';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';
import { ClientesService } from '../service/clientes.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-nuevo-cliente',
  templateUrl: './nuevo-cliente.component.html',
  styleUrls: ['./nuevo-cliente.component.css']
})
export class NuevoClienteComponent extends DinamicInput{

  formNewClient!: FormGroup;
  usedClass: string[] = [];
  title!: string[];
  ruta!: string;
  id_client!: number;
  clienteToUpdate!: client

  @ViewChild('popup', { static: true }) override popUp?: ElementRef;
  constructor(private fb: FormBuilder,
    public routes: Router,
    private comunicatorSvc$: ComunicatorComponetsService,
    private route: ActivatedRoute,
    private clientesSvc: ClientesService,
    protected override renderer: Renderer2) {
    super();
   }

  ngOnInit() {
    setTimeout(() => {
      this.usedClass[0] = "input-on-focus";
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      this.title = []
      if (this.ruta.includes("nuevo") || this.ruta.includes("cuenta")) {
        this.title.push((this.ruta.indexOf("nuevo")) ? "fas fa-cliente-cog fa-fw" : "fa fa-cliente-tie fa-fw");//añado el icono del titulo al array
      } else {
        this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2, this.routes.url.lastIndexOf('/'));
        this.title.push("fa fa-arrows-rotate");
      }
      this.title.push(decodeURI(this.ruta).toUpperCase());//añado el titulo al array
      this.comunicatorSvc$.setTitleComponent(this.title);
    });
    if (this.routes.url.includes("actualizar")) {
      this.route.paramMap.subscribe({
        next: (params: ParamMap) => {
          this.id_client = Number(params.get('id_client'));
          this.clientesSvc.getClient(this.id_client).subscribe({
            next: res => {
              if (res instanceof Array) {
                this.formNewClient.get('document_type')?.setValue(res[0].document_type)
                this.formNewClient.get('document_number')?.setValue(res[0].document_number)
                this.formNewClient.get('names')?.setValue(res[0].names)
                this.formNewClient.get('last_names')?.setValue(res[0].last_names)
                this.formNewClient.get('state')?.setValue(res[0].state)
                this.formNewClient.get('city')?.setValue(res[0].city)
                this.formNewClient.get('street')?.setValue(res[0].street)
                this.formNewClient.get('phone_number')?.setValue(res[0].phone_number)
                this.formNewClient.get('email')?.setValue(res[0].email)
                this.clienteToUpdate = res[0]
              } else {
                this.changeModal(res)
                this.popUp?.nativeElement.showModal()
              }
            },
            error: (error: HttpErrorResponse) => {
              this.error(error)
            }
          });
        }
      });
    }
    this.formNewClient = this.fb.group({
      document_number: new FormControl('',[Validators.required,Validators.pattern(/^[0-9]+$/)]),
      names: new FormControl('',[Validators.required,Validators.pattern(/^[a-zA-Z ]+$/)]),
      lastNames: new FormControl('',[Validators.required,Validators.pattern(/^[a-zA-Z ]+$/)]),
      state: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      city: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      street: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      phone_number: new FormControl('',[Validators.pattern(/^[0-9]+$/)]),
      email: new FormControl('',[Validators.email]),
    })
  }

  public error(error: HttpErrorResponse) {
    this.changeModal(this.comunicatorSvc$.errorServer(error))
    this.popUp?.nativeElement.showModal()
  }
  submit() {
    console.log(this.formNewClient.value)
    this.clientesSvc.setClient(this.formNewClient.value).subscribe({
      next: res => {
        console.log(res)
        this.changeModal(res);
        this.popUp?.nativeElement.showModal()
      },
      error: (error: HttpErrorResponse) => {
        this.error(error)
      }
    })
  }
  acept() {
    this.popUp?.nativeElement.close();
    if (this.popUp?.nativeElement.children[1].textContent === '¿Estás seguro?') {
      if (this.ruta.indexOf("actualizar")) {
        this.submit()
      } else if (this.popUp?.nativeElement.children[2].textContent.includes('¿desea continuar?')) {
        this.redirectToListClientes();
      } else {
        this.validarUpdate()
      }
    } else if (this.popUp?.nativeElement.children[1].textContent.includes('Actualizado') &&
      this.ruta.includes("actualizar")) {
      this.redirectToListClientes();
    }
  }

  update() {
    this.clientesSvc.updateClient(Number(this.id_client),
      this.formNewClient.value).subscribe({
        next: res => {
          this.changeModal(res);
          this.popUp?.nativeElement.showModal();
        },
        error: (error: HttpErrorResponse) => {
          this.error(error);
        }
      })
  }
  validarUpdate() {
    if (this.formNewClient.get('document_type')?.value === this.clienteToUpdate.document_type &&
      this.formNewClient.get('document_number')?.value === this.clienteToUpdate.document_number &&
      this.formNewClient.get('names')?.value === this.clienteToUpdate.names &&
      this.formNewClient.get('last_names')?.value === this.clienteToUpdate.last_names &&
      this.formNewClient.get('state')?.value === this.clienteToUpdate.state &&
      this.formNewClient.get('city')?.value === this.clienteToUpdate.city &&
      this.formNewClient.get('street')?.value === this.clienteToUpdate.street &&
      this.formNewClient.get('phone_number')?.value === this.clienteToUpdate.phone_number &&
      this.formNewClient.get('email')?.value === this.clienteToUpdate.email) {
      let msj: alert = {
        icon: '',
        title: '¿Estás seguro?',
        content: 'No ha modificado los datos del usuario ¿desea continuar?'
      }
      this.changeModal(msj)
      this.popUp?.nativeElement.showModal()
    } else {
      this.update()
    }
  }


  redirectToListClientes() {
    this.routes.navigate(['/administracion/lista clientes'])
  }
  clean(){
    this.formNewClient.get('document_number')?.setValue('');
    this.formNewClient.get('names')?.setValue('');
    this.formNewClient.get('lastNames')?.setValue('');
    this.formNewClient.get('state')?.setValue('');
    this.formNewClient.get('city')?.setValue('');
    this.formNewClient.get('street')?.setValue('');
    this.formNewClient.get('phone_number')?.setValue('');
    this.formNewClient.get('email')?.setValue('');
  }
}
<div class="form-container">
    <h3 class="form-title">
        <i class="far fa-address-card"></i>
        &nbsp;Información personal
    </h3>
    <div class="form" [formGroup]="formNewClient">
        <div class="form-group">
           <div class="form-control">
                <label class="form-label"
                [ngClass]="{'label-on-focus-up': elementHasFocus[0]}"
                [ngStyle]="animationLabel(formNewClient,'select')">
                    Tipo de documento
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <select name="" id="" class="form-input" 
                (focus)="isFocused(0,formNewClient,'select')"
                (focusout)="isFocused(0,formNewClient,'select')">
                    <option>Habilitada</option>
                    <option>Deshabilitada</option>
                </select>
            </div>
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'documentNumber')&&
            elementHasFocus[1],'input-on-focus-fail':!verifyStyleInput(formNewClient,'documentNumber')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[1]&&positionLabel[1],
                'label-on-focus-up':
                elementHasFocus[1]&&!positionLabel[1]}"
                [ngStyle]="animationLabel(formNewClient,
                'documentNumber')">
                    Número de documento
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input formControlName="documentNumber" autofocus
                (focus)="isFocused(1,formNewClient,'documentNumber')"
                (focusout)="isFocused(1,formNewClient,'documentNumber')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'documentNumber')" >
            </div>
            <div class="form-control" 
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'namesClient')&&
            elementHasFocus[2],'input-on-focus-fail':!verifyStyleInput(formNewClient,'namesClient')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[2]&&positionLabel[2],
                'label-on-focus-up':
                elementHasFocus[2]&&!positionLabel[2]}"
                [ngStyle]="animationLabel(formNewClient,
                'namesClient')">
                    Nombres
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input formControlName="namesClient"
                (focus)="isFocused(2,formNewClient,'namesClient')"
                (focusout)="isFocused(2,formNewClient,'namesClient')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'namesClient')">
            </div>
            <div class="form-control" 
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'lastNamesClient')&&
            elementHasFocus[3],'input-on-focus-fail':!verifyStyleInput(formNewClient,'lastNamesClient')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[3]&&positionLabel[3],
                'label-on-focus-up':
                elementHasFocus[3]&&!positionLabel[3]}"
                [ngStyle]="animationLabel(formNewClient,
                'lastNamesClient')">
                    Apellidos
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input formControlName="lastNamesClient"
                (focus)="isFocused(3,formNewClient,'lastNamesClient')"
                (focusout)="isFocused(3,formNewClient,'lastNamesClient')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'lastNamesClient')">
            </div>
        </div>
        <h3 class="form-title">
            <i class="fas fa-map-marked-alt"></i>
            &nbsp;Información de residencia
        </h3>
        <div class="form-group">
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'state')&&
            elementHasFocus[4],'input-on-focus-fail':!verifyStyleInput(formNewClient,'state')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[4]&&positionLabel[4],
                'label-on-focus-up':
                elementHasFocus[4]&&!positionLabel[4]}"
                [ngStyle]="animationLabel(formNewClient,
                'state')">
                    Estado, provincia o departamento
                </label>
                <input formControlName="state"
                (focus)="isFocused(4,formNewClient,'state')"
                (focusout)="isFocused(4,formNewClient,'state')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'state')">
            </div>
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'city')&&
            elementHasFocus[5],'input-on-focus-fail':!verifyStyleInput(formNewClient,'city')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[5]&&positionLabel[5],
                'label-on-focus-up':
                elementHasFocus[5]&&!positionLabel[5]}"
                [ngStyle]="animationLabel(formNewClient,
                'city')">
                    Ciudad o pueblo
                </label>
                <input formControlName="city"
                (focus)="isFocused(5,formNewClient,'city')"
                (focusout)="isFocused(5,formNewClient,'city')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'city')">
            </div>
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'street')&&
            elementHasFocus[6],'input-on-focus-fail':!verifyStyleInput(formNewClient,'street')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[6]&&positionLabel[6],
                'label-on-focus-up':
                elementHasFocus[6]&&!positionLabel[6]}"
                [ngStyle]="animationLabel(formNewClient,
                'street')">
                    Calle o direccion de casa
                </label>
                <input formControlName="street"
                (focus)="isFocused(6,formNewClient,'street')"
                (focusout)="isFocused(6,formNewClient,'street')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'street')">
            </div>
        </div>

        <h3 class="form-title">
            <i class="far fa-address-book"></i>
            &nbsp;Información de contacto
        </h3>

        <div class="form-group">
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'phoneNumber')&&
            elementHasFocus[7],'input-on-focus-fail':!verifyStyleInput(formNewClient,'phoneNumber')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[7]&&positionLabel[7],
                'label-on-focus-up':
                elementHasFocus[7]&&!positionLabel[7]}"
                [ngStyle]="animationLabel(formNewClient,
                'phoneNumber')">
                    Télefono
                </label>
                <input formControlName="phoneNumber"
                (focus)="isFocused(7,formNewClient,'phoneNumber')"
                (focusout)="isFocused(7,formNewClient,'phoneNumber')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'phoneNumber')">
            </div>
            <div class="form-control"
            [ngClass]="{'input-on-focus-success':verifyStyleInput(formNewClient,'email')&&
            elementHasFocus[8],'input-on-focus-fail':!verifyStyleInput(formNewClient,'email')}">
                <label class="form-label" 
                [ngClass]="{'label-on-focus-down':
                elementHasFocus[8]&&positionLabel[8],
                'label-on-focus-up':
                elementHasFocus[8]&&!positionLabel[8]}"
                [ngStyle]="animationLabel(formNewClient,
                'email')">
                    Email
                </label>
                <input formControlName="email"
                (focus)="isFocused(8,formNewClient,'email')"
                (focusout)="isFocused(8,formNewClient,'email')"
                class="form-input" [ngStyle]="styleForeground(formNewClient,'email')">
            </div>


        </div>
        <div class="form-actions">
            <button class="form-button" [ngClass]="{'btn-save':formNewClient.valid}" (click)="submit()">
                <i class="far fa-save"></i>
                &nbsp;&nbsp;ACEPTAR</button>

            <button class="form-button btn-clean" (click)="clean()">
                <i class="fas fa-paint-roller"></i>
                &nbsp;&nbsp;LIMPIAR</button>

        </div>
        <p class="form-info">Los campos marcados con <i class="fab fa-font-awesome-alt"></i> 
            son obligatorios</p>
    </div>
</div>
