import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';

@Component({
  selector: 'app-productos-por-vencimiento',
  templateUrl: './productos-por-vencimiento.component.html',
  styleUrls: ['./productos-por-vencimiento.component.css']
})
export class ProductosPorVencimientoComponent {
  constructor(private routes:Router, private comunicatorSvc:ComunicatorComponetsService){}
  ngOnInit() {
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      title.push("fas fa-history fa-fw");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
  }
}
