import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription, tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { VentasService } from '../../ventas/service/ventas.service';
import { building, coin, pay } from 'src/app/interfaces/interfaces';
import { CoinsService } from '../../configuraciones/service/coins.service';
import { EmpresaService } from '../../configuraciones/service/empresa.service';
import { Columns, ITable, Img, PdfMakeWrapper, Table, Txt } from 'pdfmake-wrapper'
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/DinamicInput';
var pdfFonts = require("pdfmake/build/vfs_fonts"); // fonts provided for pdfmake

// If any issue using previous fonts import. you can try this:
// import pdfFonts from "pdfmake/build/vfs_fonts";

// Set the fonts to use
PdfMakeWrapper.setFonts(pdfFonts);

@Component({
  selector: 'app-reporte-de-venta',
  templateUrl: './reporte-de-venta.component.html',
  styleUrls: ['./reporte-de-venta.component.css']
})
export class ReporteDeVentaComponent extends DinamicInput {
  result: any[] = [];
  data$!: Subscription
  date = new Date()
  reportes: any[] = []
  coins: coin[] = []
  paysOfCoin = [{ transaccion: 0, efectivo: 0 }]
  empresa!: building
  formGenerateReport!: FormGroup;
  constructor(private comunicatorSvc: ComunicatorComponetsService,
    private routes: Router,
    private ventasSvc: VentasService,
    private coinsSvc: CoinsService,
    private empresaSvc: EmpresaService,
    private fb: FormBuilder) {
    super();
    this.paysOfCoin = [];
  }
  ngOnInit() {
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      title.push("fas fa-hand-holding-usd fa-fw");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
    this.data$ = this.ventasSvc.getSellBydate(this.date).subscribe(res => {
      if (res instanceof Array)
        this.reportes = res
    })
    this.coinsSvc.getCoins().subscribe(coins => {
      this.coins = coins
      coins.forEach((coin: coin, i: number) => {
        this.paysOfCoin.push({ efectivo: 0, transaccion: 0 })

        this.ventasSvc.getPaysByDate(this.date, coin.symbol).subscribe(res => {
          if (res instanceof Array) {
            res.forEach((pay: pay) => {
              if (pay.type === 'Efectivo')
                this.paysOfCoin[i].efectivo += Number(pay.mount)
              else
                this.paysOfCoin[i].transaccion += Number(pay.mount)
            })
          }
        })
      })
    })
    this.empresaSvc.getBuildings().subscribe(res => {
      if (res instanceof Array) {
        this.empresa = res[0]
      }
    })
    this.formGenerateReport = this.fb.group({
      initialDate: new FormControl('', [Validators.required,
      Validators.pattern(/^\d{4}\-(0[1-9]|1[012])\-(0[1-9]|[12][0-9]|3[01])$/)]),
      endDate: new FormControl('', [Validators.required,
      Validators.pattern(/^\d{4}\-(0[1-9]|1[012])\-(0[1-9]|[12][0-9]|3[01])$/)]),
    })
  }
  calcularTotales() {
    let values = { sells: 0, total_sell: 0, total_costos: 0, ganancias: 0, coin: '' }
    this.reportes.forEach((reporte: any) => {
      values.sells += Number(reporte.sells)
      values.total_sell += Number(Number(reporte.total_sell).toFixed(2))
      values.total_costos += Number(Number(reporte.costos).toFixed(2))
      values.ganancias += Number(Number(reporte.total_sell - reporte.costos).toFixed(2))
      values.coin = reporte.coin
    })
    return values
  }
  createPDF() {
    const pdf = new PdfMakeWrapper();

    pdf.add(
      new Columns([
        new Txt(this.empresa.name).fontSize(12).lineHeight(1.4).end,
        ''
      ]).end)
    pdf.add(
      new Columns([
        new Txt(this.empresa.document_type + ' ' + this.empresa.document_number + '\n' +
          'Dirección: ' + this.empresa.address + '\n' + 'Télefono:' + this.empresa.phone_number + '\n' +
          'Email: ' + this.empresa.email).fontSize(8).end,
        ''
      ]).end)


    pdf.add(new Txt('Reporte de venta (' +
      new Intl.DateTimeFormat('es-CL').format(this.date) + ')').alignment('center')
      .margin([0, 50, 0, 20]).fontSize(10).end)
    pdf.add(this.createTables(this.extractDataSell.bind(this)))
    pdf.add(new Txt('').margin([0, 50, 0, 0]).end)
    pdf.add(this.createTables(this.extractDataTotals.bind(this)))
    pdf.add(new Txt('').margin([0, 50, 0, 0]).end)
    pdf.add(this.createTables(this.extractDataCoin.bind(this)))
    pdf.create().open()
  }
  createTables(funcion: Function): ITable {
    return new Table([
      funcion(this.reportes, this.currencyFormatter, this.calcularTotales).header,
      ...funcion(this.reportes, this.currencyFormatter).body
    ]).alignment('center').fontSize(8).widths('*').heights(rowIndex => {
      return rowIndex === 0 ? 13 : 0
    }).layout({
      fillColor(rowIndex: number | undefined, node: any, columnIndex: number | undefined): string {
        return rowIndex === 0 ? '#cccccc' : ''
      },
    }).end
  }
  extractDataSell() {
    let aux = ['Caja', 'Vendedor', 'Ventas realizadas', 'Total en ventas', 'Costos de ventas', 'Ganancias'];
    let other = this.reportes.map((reporte: any) => [
      reporte.name, reporte.names_user + ' ' + reporte.last_names_user,
      reporte.sells,
      this.currencyFormatter(reporte.total_sell),
      this.currencyFormatter(reporte.costos),
      this.currencyFormatter(reporte.total_sell - reporte.costos)
    ])
    return { header: aux, body: other }
  }
  extractDataTotals() {
    let aux = ['Total ventas realizadas', 'Total en ventas', 'Total costos de ventas', 'Total ganancias'];
    let other = this.reportes.map((reporte: any) => [
      this.calcularTotales().sells,
      this.currencyFormatter(this.calcularTotales().total_sell),
      this.currencyFormatter(this.calcularTotales().total_costos),
      this.currencyFormatter(this.calcularTotales().total_sell - this.calcularTotales().total_costos)
    ])
    return { header: aux, body: other }
  }
  extractDataCoin() {
    let aux = ['Moneda', 'Total en transacciones eléctronicas', 'Total en efectivo'];
    let other = this.coins.map((coin: any, index: number) => [
      coin.name + ' ' + coin.symbol,
      this.currencyFormatter(this.paysOfCoin[index].transaccion),
      this.currencyFormatter(this.paysOfCoin[index].efectivo),

    ])
    return { header: aux, body: other }
  }
  currencyFormatter(value: number) {
    const formatter = new Intl.NumberFormat('es-VE', {
      style: 'currency',
      minimumFractionDigits: 2,
      currency: 'VES',
    })
    return formatter.format(value)
  }
  verificarFechas() {
    let result = { isValid: false, msj: '' }
    if (this.formGenerateReport.get('initialDate')?.invalid) {
      result = { isValid: false, msj: 'La fecha inicial es invalida' }
    } else if (this.formGenerateReport.get('endDate')?.invalid) {
      result = { isValid: false, msj: 'La fecha final es invalida' }
    } else if (this.formGenerateReport.get('endDate')?.value <= this.formGenerateReport.get('initialDate')?.value) {
      result = { isValid: false, msj: 'La fecha final debe ser mayor a la inicial' }
    } else {
      result = { isValid: true, msj: '' }
    }
    return result
  }
  ngOnDestroy() {
    this.data$.unsubscribe();
  }
}
<h3 class="report-title">Estadísticas de venta hoy ({{date|date:'dd/MM/yyyy'}})</h3>
<div class="table-body">
    <div class="table-responsive">
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Caja</th>
                    <th>Vendedor</th>
                    <th>Ventas realizadas</th>
                    <th>Total en ventas</th>
                    <th>Costo de venta</th>
                    <th>Ganancias</th>
                    <th></th>
                </tr>
            </thead>

            <tbody>
                 <tr *ngFor="let reporte of reportes">
                    <td>{{reporte.name}}</td>
                    <td>{{reporte.names_user}} {{reporte.last_names_user}}</td>
                    <td>{{reporte.sells}}</td>
                    <td>{{reporte.total_sell|currency:reporte.coin+' '}}</td>
                    <td>{{reporte.costos|currency:reporte.coin+' '}}</td>
                    <td>{{reporte.total_sell-reporte.costos|currency:reporte.coin+' '}}</td>
                </tr>
                <tr *ngIf="reportes.length<1">
                    <td colspan="7">No hay ventas realizadas el dia de hoy</td>
                </tr> 
            </tbody>
        </table>
    </div>
</div>
<div class="table-body">
    <div class="table-responsive">
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Total ventas realizadas</th>
                    <th>Total en ventas</th>
                    <th>Total costos de venta</th>
                    <th>Total ganancias</th>
                </tr>
            </thead>

            <tbody >
                <tr *ngIf="reportes.length>0">
                    <td>{{calcularTotales().sells}}</td>
                    <td>{{calcularTotales().total_sell|currency:calcularTotales().coin+' '}}</td>
                    <td>{{calcularTotales().total_costos|currency:calcularTotales().coin+' '}}</td>
                    <td>{{calcularTotales().ganancias|currency:calcularTotales().coin+' '}}</td>
                </tr>
                <tr *ngIf="reportes.length<1">
                    <td colspan="7">No hay ventas realizadas el dia de hoy</td>
                </tr> 
            </tbody>
        </table>
    </div>
</div>
<div class="table-body">
    <div class="table-responsive">
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Moneda</th>
                    <th>Total en transacciones eléctronicas</th>
                    <th>Total en efectivo</th>
                </tr>
            </thead>

              <tbody *ngIf="paysOfCoin.length > 0">
                <tr *ngFor="let coin of coins; let i=index">
                    <td>{{coin.name}} {{coin.symbol}}</td>
                    <td >{{paysOfCoin[i].transaccion|currency:coin.symbol+' '}}</td>
                    <td >{{paysOfCoin[i].efectivo|currency:coin.symbol+' '}}</td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
<div class="form-actions">
    <button class="form-button btn" (click)="createPDF()"
    [ngClass]="{'enable':reportes.length>0}">
        <i class="fas fa-print"></i>&nbsp;&nbsp;IMPRIMIR</button>

</div>
<div class="line"></div>
<div class="form-container">
    <h3 class="form-title">
        &nbsp;Generar reporte por fecha
    </h3>
    <div class="form" [formGroup]="formGenerateReport">
        <div class="form-group">
            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[0]}">
                <label class="form-label" [ngClass]="{'label-on-focus-up':
                elementHasFocus[0]}" [ngStyle]="animationLabel(formGenerateReport,
                'select')">
                    Fecha de inicio
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input type="date" formControlName="initialDate" (focus)="isFocused(0,formGenerateReport,
                'select')" (focusout)="isFocused(0,formGenerateReport,'select')" class="form-input">
            </div>
            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[1]}">
                <label class="form-label" [ngClass]="{'label-on-focus-up':
            elementHasFocus[1]}" [ngStyle]="animationLabel(formGenerateReport,
            'select')">
                     Fecha de fin
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input type="date" formControlName="endDate" (focus)="isFocused(1,formGenerateReport,'select')"
                    (focusout)="isFocused(1,formGenerateReport,'select')" class="form-input"
                    min="{{formGenerateReport.get('initialDate')?.value|date:'yyyy-MM-dd'}}">
            </div>
        </div>
        <div class="form-actions">
            <button class="form-button" title="{{verificarFechas().msj}}"
            (click)="createPDF()"
            [ngClass]="{'enable':verificarFechas().isValid}">
                <i class="far fa-save"></i>
                &nbsp;&nbsp;GENERAR REPORTE</button>
        </div>
    </div>
</div>
