import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nueva-caja',
  templateUrl: './nueva-caja.component.html',
  styleUrls: ['./nueva-caja.component.css']
})

export class NuevaCajaComponent extends DinamicInput {

  nuevaCaja!: FormGroup;

  constructor(private fb: FormBuilder) {
    super();
  }

  ngOnInit() {
    this.nuevaCaja = this.fb.group({
      entradaNumeroCaja: new FormControl('', [Validators.required])
    })
  }
  /* submit() {
    /* if (this.nuevaCaja.valid)
      this.resultado = "Todos los datos son v치lidos";
    else
      this.resultado = "Hay datos inv치lidos en el formulario";
  } */
  validator() {

    let styles;
    if (this.nuevaCaja.get('entradaNumeroCaja')?.errors) {
      styles = {
        "background-color": "red"
      }
    }else{
      styles = {
        "background-color": "#009688"
      }
    }
  }
}
