import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ComunicatorComponetsService {
  private listData$: BehaviorSubject<any[]>=new BehaviorSubject<any[]>([]);
  private state$: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(true);
  private titleComponent$: BehaviorSubject<string[]> = new BehaviorSubject<string[]>([]);
  private targetToUpdate$: BehaviorSubject<string[]> = new BehaviorSubject<string[]>([]);
  private apiURL='http://localhost:3000/registers';

  constructor(private http:HttpClient) { }

  public setshowHideNavBar(state: boolean) {
    this.state$.next(state);
  }

  public getShowHideNavBar(): Observable<boolean> {
    return this.state$.asObservable();
  }

  public setTitleComponent(title:any){
    this.titleComponent$.next(title);
  }
  public getTitleComponent():Observable<string[]>{
    return this.titleComponent$.asObservable();
  }

  public setData(data:any){
    this.listData$.next(data)
  }
  public getData(){
    return this.listData$.asObservable();
  }
  public setTargetToUpdate(target:any){
    this.targetToUpdate$.next(target)
  }
  public getTargetToUpdate(){
    return this.targetToUpdate$.asObservable();
  }
  public getRegisters():Observable<any>{
    return this.http.get(this.apiURL);
  }
  public getRegister(id_register:number):Observable<any>{
    return this.http.get(this.apiURL+'/'+id_register);
  }
  public setRegister(register:any):Observable<any>{
   return this.http.post<any>(this.apiURL,register)
  }
  public updateRegister(id_register:number,register:any):Observable<any>{
    return this.http.put<any>(this.apiURL+'/'+id_register,register)
  }
  public deleteRegister(id_register:number):Observable<any>{
    return this.http.delete(this.apiURL+'/'+id_register)
   }
}
