import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-nueva-caja',
  templateUrl: './nueva-caja.component.html',
  styleUrls: ['./nueva-caja.component.css']
})

export class NuevaCajaComponent {
  focus: Array<boolean> = new Array(2);
  labelFocus: Array<boolean> = new Array(2);
  nuevaCaja!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.nuevaCaja = this.fb.group({
      entradaNumeroCaja: new FormControl('hola')
    })
  }
  isFocused(index: number) {
    this.focus[index] = !this.focus[index];
    if (this.nuevaCaja.get('entradaNumeroCaja')?.value) {
      this.labelFocus[0]=false;
    }else {
      this.labelFocus[0]=true;
    }
  }
  labelOnFocus() {
    let styles!: object;
    if (this.nuevaCaja.get('entradaNumeroCaja')?.value) {
      this.labelFocus[0]=false;
      styles = {
        "color": "#009688",
      }
    } else {
      this.labelFocus[0]=true;
      styles = {
        "animation-name": "label",
        "animation-duration": ".3s",
        "animation-fill-mode": "both",
        "color": "#009688"
      }
    }
    return styles;
  }
  animationLabel() {
    let styles!: object;
    if (this.nuevaCaja.get('entradaNumeroCaja')?.value) {
      styles = {
        "top": "0",
        "font-size": "1rem",
      }
    } else {
      styles = {
        "top": "20px"
      }
    }
    return styles;
  }
}
