import { DOCUMENT } from '@angular/common';
import { Component, Inject } from '@angular/core';

@Component({
  selector: 'app-cajas',
  templateUrl: './cajas.component.html'
})
export class CajasComponent {
  nuevaCaja:boolean;
  listaCajas :boolean;
  constructor(@Inject(DOCUMENT) document: any) {
    this.listaCajas = String(document.location.href).includes('lista');
    this.nuevaCaja = String(document.location.href).includes('nueva')

  }

  selected(param1: boolean, param2: boolean) {
    this.nuevaCaja = param1;
    this.listaCajas = param2;
  }
  selectedClass(param: boolean) {
    let selectedClass = {
      'sub-option-active': param,
      'sub-option': !param
    };
    return selectedClass;
  }

}
