import { Component } from '@angular/core';

@Component({
  selector: 'app-productos-por-categoria',
  templateUrl: './productos-por-categoria.component.html',
  styleUrls: ['./productos-por-categoria.component.css']
})
export class ProductosPorCategoriaComponent {


}
<div class="main-container">
    <div class="left-container">

        <h3><i class="fas fa-tags"></i> &nbsp;CATEGORÍAS</h3>
    </div>
    <div class="right-container">
        <div class="presentation">
            <i class="fab fa-shopify fa-fw fa-5x"></i>
            <h3>Categoría no seleccionada</h3>
            <p>Por favor seleccione una categoría para empezar a buscar productos.</p>
        </div>
    </div>    
</div>
