import { Component } from '@angular/core';

@Component({
  selector: 'app-cajas',
  templateUrl: './cajas.component.html',
  styleUrls: ['./cajas.component.css']
})
export class CajasComponent {
  nuevaCaja!:boolean;
  listaCajas!:boolean;

  change(param:boolean){
    param=!param;
  }
}
