import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable, Subscription, filter, observable, tap } from 'rxjs';
import { TableFilterPipe } from 'src/app/pipes/table-filter.pipe';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicTable } from 'src/app/utils/DinamicTable';

@Component({
  selector: 'app-lista-cajas',
  templateUrl: './lista-cajas.component.html'
})
export class ListaCajasComponent extends DinamicTable {


  tableSearch!: FormGroup;
  result: any[] = [];
  currentData: any[] = [];
  data$!: Subscription;
  title: string[] = [];

  //Elemento donde se insertaran los numeros 
  //de paginas para la paginacion
  @ViewChild('pagination', { static: false }) override pagination?: ElementRef;
  //Elemento antes del cual se comenzaran a insertar las paginas
  @ViewChild('next', { static: false }) override next?: ElementRef;
  @ViewChild('dataTables_info', { static: false }) override info?: ElementRef;
  @ViewChild('rows', { static: false }) row?: ElementRef;

  constructor(private fb: FormBuilder,
    public override renderer: Renderer2,
    private comunicatorSvc: ComunicatorComponetsService, private routes: Router,
    private other: ComunicatorComponetsService) {
    super(renderer)
  }
  ngOnInit() {
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      let ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      this.title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      this.title.push("fas fa-clipboard-list fa-fw");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(this.title);
    });
    this.data$ = this.comunicatorSvc.getRegisters().subscribe(
      {
        next: res => this.result = res,
        error: error => console.log("error" + error),
        complete: () => {
          this.op()

        }
      }
    );

    this.tableSearch = new FormGroup({
      search: new FormControl(),
      numberRows: new FormControl('10')
    });
  }

  op(){
//me suscribo a la data actual. la data actual cambiara segun se apliquen filtros de busqueda
this.other.getData().subscribe(
  {
    next: res => this.currentData = res,
    //inserto las paginas al DOM
    complete: () => {
      console.log(this.currentData)
      this.createPages(this.tableSearch, this.currentData, this.result)
    }
  })
  }

  update(data: any) {
    this.routes.navigate(['/administracion/actualizar caja'])
    console.log("updating" + data.name + data.id_register)
  }
  delete(data: any) {
    let index = this.result.indexOf(data)
    this.result.splice(index, 1)
    this.currentData.splice(index, 1)
    this.renderer.removeChild(this.row?.nativeElement, this.row?.nativeElement.children[index])
    this.comunicatorSvc.deleteRegister(data.id_register).subscribe()
    this.createPages(this.tableSearch, this.currentData, this.result)
    console.log("deleting")
  }
  ngOnDestroy() {
    this.data$.unsubscribe();
  }

}
