import { DOCUMENT } from '@angular/common';
import { Component, Inject } from '@angular/core';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicComponent } from 'src/app/utils/DinamicComponent';

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
}) 
export class UsuariosComponent extends DinamicComponent{
  constructor(@Inject(DOCUMENT) document: any,private routes:Router,
  private comunicatorSvc:ComunicatorComponetsService) {
    super(String(document.location.href).includes('nuevo'),
    String(document.location.href).includes('lista'))
  } 
  ngOnInit(){
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      title.push(""fas fa-user-cog fa-fw"");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
  }
}
<div class="scroll info-container container">
    <h2 class="title">
        <i class="fa fa-shipping-fast fa-fw">
        </i> &nbsp;NUEVO USUARIO
    </h2>
    <p class="msj">En el módulo USUARIOS podrá registrar 
        las empresas a los cuales usted les compra 
        nuevos usuarios en el sistema, ya sea un administrador o 
        un cajero, también podra ver la lista de usuarios 
        registrados, buscar usuarios en el sistema y actualizar 
        datos de otros usuarios
    </p>
    <ul class="list-options">
        <li (click)="selected(true,false)" 
        [ngClass]="selectedClass(option1)">
            <a routerLink="nuevo usuario" 
            [class.active]="option1" class="option-component">
                <div class="container">
                    <i class="fa fa-user-tie fa-fw"></i>
                    &nbsp;&nbsp;NUEVO USUARIO
                </div>
            </a>
        </li>
        <li (click)="selected(false,true)" 
        [ngClass]="selectedClass(option2)">
            <a routerLink="lista usuarios" 
            [class.active]="option2" class="option-component">
                <div class="container">
                    <i class="fa fa-user-tie fa-fw"></i>
                    &nbsp;&nbsp;LISTA DE USUARIOS
                </div>
            </a>
        </li>
    </ul>
    <router-outlet></router-outlet>
</div>
