import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';

@Component({
  selector: 'app-reporte-de-venta',
  templateUrl: './reporte-de-venta.component.html',
  styleUrls: ['./reporte-de-venta.component.css']
})
export class ReporteDeVentaComponent {
  result: any[] = [];
  constructor(private comunicatorSvc: ComunicatorComponetsService, private routes: Router) {}
  ngOnInit() {
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      title.push("fas fa-hand-holding-usd fa-fw");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
    this.comunicatorSvc.getData().
      pipe(tap(res => this.result = res))
      .subscribe();

  }
}

<div class="table-container">
    <div class="table-title">
        <h6>Productos por vencimiento</h6>
    </div>
    <div class="table-body">
        <div class="table-responsive">
            <table class="table table-bordered" >
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Office</th>
                        <th>Age</th>
                        <th>Start date</th>
                        <th>Salary</th>
                    </tr>
                </thead>

                <tbody>
                    <tr *ngFor="let data of data | 
                    tableFilter:getValuesToPipe(tableSearch)">
                        <td>{{data.name}}</td>
                        <td>{{data.position}}</td>
                        <td>{{data.office}}</td>
                        <td>{{data.age}}</td>
                        <td>{{data.startDate}}</td>
                        <td>{{data.salary}}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
</div>
