import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup, ValidationErrors, ValidatorFn, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { user } from 'src/app/interfaces/interfaces';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';
import { ProveedoresService } from '../../proveedores/service/proveedores.service';

@Component({
  selector: 'app-nuevo-usuario',
  templateUrl: './nuevo-usuario.component.html',
  styleUrls: ['./nuevo-usuario.component.css']
})
export class NuevoUsuarioComponent extends DinamicInput {
  formNewUser!: FormGroup;
  usedClass: string[] = [];
  title!: string[];
  ruta!: string;
  id_user!: number;
  userToUpdate!: user
  @ViewChild('popup', { static: true }) override popUp?: ElementRef;

  constructor(private fb: FormBuilder,
    public routes: Router,
    private comunicatorSvc$: ComunicatorComponetsService,
    private route: ActivatedRoute,
    private providersSvc: ProveedoresService,
    protected override renderer: Renderer2) {
    super();
  }

  ngOnInit() {
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      console.log(this.ruta)
      switch (this.ruta) {
        case "nuevo usuario":
          this.title.push("fa fa-user-tie fa-fw");//añado el icono del titulo al array
          break;
        case "actualizar cuenta":
          this.title.push("fas fa-user-cog fa-fw");//añado el icono del titulo al array
          break;
        default:
          this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2, this.routes.url.lastIndexOf('/'));
          this.title.push("fa fa-arrows-rotate");
          break;

      }
     /*  if (this.ruta.includes("nuevo")) {
        this.title.push("fas fa-user-cog fa-fw");//añado el icono del titulo al array
      } else if (this.ruta.includes("actualizar")) {
        this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2, this.routes.url.lastIndexOf('/'));
        this.title.push("fa fa-arrows-rotate");
      } else {

      }
      title.push((ruta.indexOf("nuevo")) ? "fas fa-user-cog fa-fw" : "fa fa-user-tie fa-fw");//añado el icono del titulo al array
      */ this.title.push(decodeURI(this.ruta).toUpperCase());//añado el titulo al array
      this.comunicatorSvc$.setTitleComponent(this.title);
    });
    this.formNewUser = this.fb.group({
      documentNumber: new FormControl('', [Validators.required, Validators.pattern(/^[0-9]+$/)]),
      namesUser: new FormControl('', [Validators.required, Validators.pattern(/^[a-zA-Z ]+$/)]),
      lastNamesUser: new FormControl('', [Validators.required, Validators.pattern(/^[a-zA-Z ]+$/)]),
      phoneNumber: new FormControl('', [Validators.required, Validators.pattern(/^[0-9]+$/)]),
      email: new FormControl('', [Validators.pattern(/^[a-zA-Z ]+$/)]),
      password: new FormControl('', [Validators.required]),
      repeatPassword: new FormControl('', [Validators.required]),
    },

    )
    this.formNewUser.get('repeatPassword')?.setValidators(
      [this.equalsValidator(this.formNewUser, 'password'), Validators.required])
    this.formNewUser.get('password')?.setValidators(
      [this.equalsValidator(this.formNewUser, 'repeatPassword'), Validators.required])
  }
  submit() {
    if (this.formNewUser.valid)
      console.log("registrd")
  }
  clean() {
    this.formNewUser.get('documentNumber')?.setValue('');
    this.formNewUser.get('namesUser')?.setValue('');
    this.formNewUser.get('lastNamesUser')?.setValue('');
    this.formNewUser.get('phoneNumber')?.setValue('');
    this.formNewUser.get('email')?.setValue('');
    this.formNewUser.get('password')?.setValue('');
    this.formNewUser.get('repeatPassword')?.setValue('');
  }
  equalsValidator(otherControl: AbstractControl, equalCamp: string): ValidatorFn {
    return (control: AbstractControl): ValidationErrors | null => {
      const value: any = control.value;
      const otherValue: any = otherControl.get(equalCamp)?.value;
      console.log(value + "   " + otherValue)
      if (otherValue.length < 1)
        return null
      else {
        if (otherValue === value)
          otherControl.get(equalCamp)?.setErrors(null)
        return otherValue === value ? null : { 'notEquals': { value, otherValue } };
      }
    };
  }


}
