import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-nueva-caja',
  templateUrl: './nueva-caja.component.html'
})

export class NuevaCajaComponent {
  focus:boolean []= [];
  positionLabel: boolean[] = [];
  nuevaCaja!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.nuevaCaja = this.fb.group({
      entradaNumeroCaja: new FormControl('')
    })
  }
  isFocused(index: number,form:FormGroup,label:string) {
    this.focus[index] = !this.focus[index];
    this.positionLabel[index]=form.get(label)?.value
    ? false:true
  }

  animationLabel(input:string,form:FormGroup) {
    let styles!: object;
    if (form.get(input)?.value||input==='select') {
      styles = {
        "top": "0",
        "font-size": "1rem",
      }
    } 
    return styles;
  }
}
