import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';

@Component({
  selector: 'app-warning',
  templateUrl: './warning.component.html',
  styleUrls: ['./warning.component.css']
})
export class WarningComponent {
  @ViewChild('popup', { static: false }) popUp?: ElementRef;

  constructor(private renderer:Renderer2){}

  setValues(values:string[]){
    this.renderer.setProperty(this.popUp?.nativeElement.children[0],'textContent',values[0])
    this.renderer.setProperty(this.popUp?.nativeElement.children[1],'textContent',values[1])
    this.renderer.setProperty(this.popUp?.nativeElement.children[2],'textContent',values[2])
  }
  showModal(){
    this.popUp?.nativeElement.showModal();
  }
  closeModal(){
    this.popUp?.nativeElement.close();
  }
}
