import { FormGroup } from "@angular/forms";

export class DinamicComponent {
  option1: boolean;
  option2: boolean;

  constructor(param1: boolean, param2: boolean) {
    this.option1 = param1;
    this.option2 = param2;
  }

  selected(param1: boolean, param2: boolean) {
    this.option1 = param1;
    this.option2 = param2;
  }
  selectedClass(param: boolean) {
    let selectedClass = {
      'sub-option-active': param,
      'sub-option': !param
    };
    return selectedClass;
  }

}
export class DinamicInput{

  elementHasFocus: boolean[] = [];
  positionLabel: boolean[] = [];

  isFocused(index: number, form: FormGroup, label: string) {
    this.elementHasFocus[index] = !this.elementHasFocus[index];
    this.positionLabel[index] = form.get(label)?.value
      ? false : true
  }

  animationLabel(form: FormGroup, input: string,) {
    let styles!: object;
    if (form.get(input)?.value || input === 'select') {
      styles = {
        "top": "0",
        "font-size": "1rem",
      }
    }
    return styles;
  }
}
