import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nuevo-producto',
  templateUrl: './nuevo-producto.component.html',
  styleUrls: ['./nuevo-producto.component.css']
})
export class NuevoProductoComponent extends DinamicInput{
  formNewProduct!:FormGroup;
  constructor(private fb:FormBuilder){
    super();
  }
  ngOnInit(){
    this.formNewProduct=this.fb.group(
      {
        codigoBarra:new FormControl(''),
      }
    )
  }

}
<div class="form-container">
    <h3 class="form-title">
        <i class="fab fa-font-awesome-alt"></i>
        &nbsp;Código de barra
    </h3>
    <div class="form" [formGroup]="formNewProduct">
        <div class="form-group">
            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[0]}">
                <label class="form-label" [ngClass]="{'label-on-focus-down':
                elementHasFocus[0]&&positionLabel[0],
                'label-on-focus-up':
                elementHasFocus[0]&&!positionLabel[0]}" [ngStyle]="animationLabel(formNewProduct,
                'codigoBarra')">
                    Código de barra
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input formControlName="codigoBarra" (focus)="isFocused(0,formNewProduct,'codigoBarra')"
                    (focusout)="isFocused(0,formNewProduct,'codigoBarra')" class="form-input">
            </div>
        </div>
        <h3 class="form-title">
            <i class="fas fa-box fa-fw"></i>
            &nbsp;Información del producto
        </h3>
    </div>
</div>
