  product(repayment: repayment) {
    let name_product = ''
    this.products.forEach((product: product) => {
      if (product.id_product === repayment.id_product) {
        name_product = product.name + ' ' + product.mark + ' ' + product.model
      }
    })
    return name_product
  }
