      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    }
  ]
  tableSearch!: FormGroup;
  private numberPages?: number;
  indexCurrentPage: number = 0;
  currentPage?:any;

  constructor(private fb: FormBuilder, private renderer: Renderer2) { }
  //Elemento donde se insertaran los numeros 
  //de paginas para la paginacion
  @ViewChild('pagination', { static: false }) pagination?: ElementRef;
  //Elemento antes del cual se comenzaran a insertar las paginas
  @ViewChild('next', { static: false }) next?: ElementRef;

  //Se insertan las paginas al DOM
  ngAfterViewInit() {
    this.changes();
  }

  deleteChildren() {
    let nodes = this.pagination?.nativeElement.childNodes;
    if (nodes.length > 2) {
      for (let index = 1; index < nodes.length - 1;) {
        this.renderer.removeChild(this.pagination?.nativeElement,
          this.pagination?.nativeElement.children[1])
      }
    }
  }

  AddDeleteStyles(classOne: string, classTwo: string) {
    this.renderer.removeClass(this.currentPage, classOne)
    this.renderer.addClass(this.currentPage, classTwo)

  }

  changes() {
    this.deleteChildren();
    this.numberPages = Math.trunc((this.data.length - 1) /
      this.tableSearch.get('numberRows')?.value) + 1

    for (let i = 0; i < this.numberPages; i++) {
      const child = this.renderer.createElement('li')
      const value = this.renderer.createText((i + 1).toString())
      this.renderer.addClass(child, 'dataTables_info')
      this.renderer.setProperty(child, 'value', i)
      this.renderer.listen(child, 'click', (event) => {
        this.AddDeleteStyles('selected', 'items')
        this.currentPage = child;
        this.AddDeleteStyles('items', 'selected')
      })
      if (i === 0) {
        this.renderer.addClass(child, 'selected')
        this.currentPage = child;
      } else {
        this.renderer.addClass(child, 'items')

      }
      this.renderer.addClass(child, 'page-item')
      this.renderer.appendChild(child, value)
      this.renderer.insertBefore(this.pagination?.nativeElement,
        child, this.next?.nativeElement)
    }

  }

  ngOnInit() {
    this.tableSearch = new FormGroup({
      search: new FormControl(),
      numberRows: new FormControl('10')
    });

  }
  getValues(): object {
    let argsPipe = {
      search: this.tableSearch.get('search')?.value,
      indexCurrentPage: (this.currentPage===undefined)?
      0:Number(this.currentPage.value),
      numberRows:Number( this.tableSearch.get('numberRows')?.value)
    }
    return argsPipe;
  }
}
