import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { filter } from 'rxjs';

@Component({
  selector: 'app-lista-cajas',
  templateUrl: './lista-cajas.component.html',
  styleUrls: ['./lista-cajas.component.css']
})
export class ListaCajasComponent {
  tableSearch!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.tableSearch = new FormGroup({
      search: new FormControl(),
      numberRows:new FormControl('10')
    });
    console.log(this.tableSearch.get('numberRows')?.value);

  }
  data = [
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    }
  ]
}
