import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicTable } from 'src/app/utils/DinamicTable';

@Component({
  selector: 'app-lo-mas-vendido',
  templateUrl: './lo-mas-vendido.component.html',
  styleUrls: ['./lo-mas-vendido.component.css']
})
export class LoMasVendidoComponent extends DinamicTable {

  data = [
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    }
  ]
  tableSearch!: FormGroup;
  result: any[] = [];
  //Elemento donde se insertaran los numeros 
  //de paginas para la paginacion
  @ViewChild('pagination', { static: false }) override pagination?: ElementRef;
  //Elemento antes del cual se comenzaran a insertar las paginas
  @ViewChild('next', { static: false }) override next?: ElementRef;
  @ViewChild('dataTables_info', { static: false }) override info?: ElementRef;

  constructor(private fb: FormBuilder,
    public override renderer: Renderer2,
    private comunicatorSvc: ComunicatorComponetsService,private routes:Router) {
    super(renderer)
  }
  ngOnInit() {
    let title=[];
    /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
    let ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
    ruta=ruta.replaceAll('%20',' ').toUpperCase();
    title.push(ruta);//añado el titulo al array
    title.push("fa fa-fire-alt fa-fw");//aañado el icono del titulo al array
    this.comunicatorSvc.setTitleComponent(title);
    this.comunicatorSvc.getData().
      pipe(tap(res => this.result = res))
      .subscribe();

    this.tableSearch = new FormGroup({
      numberRows: new FormControl('10')
    });
  }
  //Se insertan las paginas al DOM
  ngAfterViewInit() {
    this.createPages(this.tableSearch,this.result,this.data);
  }

}

<div class="table-container">
    <div class="table-title">
        <h6>Productos en el almacen</h6>
    </div>
    <div class="table-body">
        <div class="search-pagination" [formGroup]="tableSearch">
            <div class="dataTable-length">
                <label>Mostrar
                    <select name="dataTable_length" 
                    formControlName="numberRows" 
                    class="custom-select"
                        (change)="createPages(tableSearch,result,data)">
                        <option value="2">2</option>
                        <option value="10">10</option>
                        <option value="25">25</option>
                        <option value="50">50</option>
                        <option value="100">100</option>
                    </select> entradas</label>
            </div>
            <div class="dataTable-filter">
                <label>Buscar:
                    <input formControlName="search" 
                    class="input-search dataTable-filter" 
                    (keyup)="createPages(tableSearch,result,data)">
                </label>
            </div>
        </div>
        <div class="table-responsive">
            <table class="table table-bordered" >
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Office</th>
                        <th>Age</th>
                        <th>Start date</th>
                        <th>Salary</th>
                    </tr>
                </thead>

                <tbody>
                    <tr *ngFor="let data of data | 
                    tableFilter:getValuesToPipe(tableSearch)">
                        <td>{{data.name}}</td>
                        <td>{{data.position}}</td>
                        <td>{{data.office}}</td>
                        <td>{{data.age}}</td>
                        <td>{{data.startDate}}</td>
                        <td>{{data.salary}}</td>
                    </tr>
                </tbody>
            </table>
        </div>
        <div class="search-pagination">
            <div class="dataTables_info" #dataTables_info>
            </div>
            <div class="pagination-responsive">
                <ul class="pagination" #pagination>
                    <li class="previous page-item dataTables_info items"
                        [class.disable]="getValuesToPipe(tableSearch).indexCurrentPage===0"
                        [class.active]="getValuesToPipe(tableSearch).indexCurrentPage!=0" 
                        (click)="previousPage(tableSearch,result,data)">
                        Previous
                    </li>

                    <li class="next dataTables_info active items" #next
                        [class.disable]="getValuesToPipe(tableSearch).indexCurrentPage===numberPages-1" 
                        (click)="nextPage(tableSearch,result,data)">
                        Next
                    </li>
                </ul>
            </div>
        </div>
    </div>
</div>
