  equalsValidator(otherControl: AbstractControl): ValidatorFn {
    return (control: AbstractControl) => {
      const value: any = control.value;
      const otherValue: any = otherControl.get('password')?.value;
      console.log(value+"   "+otherValue)
      if (value !== otherValue) {
        console.log("pongo erro")
        otherControl.setErrors({ mustMatch: true });
      } else {
        otherControl.setErrors(null);
      }
      return null;
    };
  }
  static mustMatch(controlName: string, matchingControlName: string) {
    console.log("valor")
    return (formGroup: FormGroup) => {
      console.log("valor dentro")
      const control = formGroup.controls[controlName];
      const matchingControl = formGroup.controls[matchingControlName];

      if (matchingControl.errors && !matchingControl.errors['mustMatch']) {
        return;
      }

      // set error on matchingControl if validation fails
      console.log("valor del control"+control.value)
      if (control.value !== matchingControl.value) {
        matchingControl.setErrors({ mustMatch: true });
      } else {
        matchingControl.setErrors(null);
      }
      return null;
    };
  }
