import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent {
  public ruta!: string;

  constructor(private routes: Router) {
    this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2)
    this.ruta=this.ruta.replaceAll('%20',' ')
    console.log('mi ruta' + this.ruta)
  }
}
<div class="scroll info-container container">
    <h2 class="title">
        <i class="fas fa-box fa-fw"></i>
        &nbsp;{{ruta}}
    </h2>
    <p class="msj">En el módulo PRODUCTOS usted podrá agregar nuevos productos al sistema, actualizar datos de los productos,
        buscar productos en el sistema, ver todos los productos en el almacén, ver los productos mas vendidos 
        y filtrar productos por categoría  
    </p>

    <router-outlet></router-outlet>      
</div>
