import { DOCUMENT } from '@angular/common';
import { Component, Inject } from '@angular/core';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicComponent } from 'src/app/utils/DinamicComponent';

@Component({
  selector: 'app-cajas',
  templateUrl: './cajas.component.html'
})

export class CajasComponent extends DinamicComponent {
  title: string[] = []
  v = true;
  f = false
  constructor(@Inject(DOCUMENT) document: any, private comunicatorSvc: ComunicatorComponetsService) {
    /* super(String(document.location.href).includes('nueva'),
    String(document.location.href).includes('lista')) */
    super(true, false);
  }
  /* override selected(param1: boolean, param2: boolean): void {
    if (this.title[0].includes("NUEVA")) {
      this.option1 = true
      this.option2=false
    }else{
      this.option1 = false
      this.option2=true
    }
    console.log(this.title[0])
  } */

  ngOnInit() {
    this.comunicatorSvc.getTitleComponent().subscribe(res => this.title = res,
      error=>console.log(error)),
      ()=>{this.selected(String(document.location.href).includes('nueva'),
      String(document.location.href).includes('lista'))}

  }
}
