import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Subscription, tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicTable } from 'src/app/utils/DinamicTable';

@Component({
  selector: 'app-kardex-general',
  templateUrl: './kardex-general.component.html',
  styleUrls: ['./kardex-general.component.css']
})
export class KardexGeneralComponent extends DinamicTable{


  data = [
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
