import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { filter, tap } from 'rxjs';
import { TableFilterPipe } from 'src/app/pipes/table-filter.pipe';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';

@Component({
  selector: 'app-lista-cajas',
  templateUrl: './lista-cajas.component.html',
  styleUrls: ['./lista-cajas.component.css']
})
export class ListaCajasComponent {
  data = [
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Tiger Nixon",
      position: 'System Architect',
      office: 'Edinburgh',
      age: 61,
      startDate: '2011/04/25',
      salary: '$320,800',
    },
    {
      name: "Black Adams",
      position: 'System Engineer',
      office: 'Illinois',
      age: 21,
      startDate: '2011/12/25',
      salary: '$120,300',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    },
    {
      name: "Woods Ada",
      position: 'Secretary',
      office: 'England',
      age: 61,
      startDate: '2011/04/25',
      salary: '$50,800',
    }
  ]
  tableSearch!: FormGroup;
  numberPages: number = 0;
  currentPage?: any;
  result: any[] = [];

  constructor(private fb: FormBuilder,
    private renderer: Renderer2,
    private comunicatorSvc: ComunicatorComponetsService) { }
  //Elemento donde se insertaran los numeros 
  //de paginas para la paginacion
  @ViewChild('pagination', { static: false }) pagination?: ElementRef;
  //Elemento antes del cual se comenzaran a insertar las paginas
  @ViewChild('next', { static: false }) next?: ElementRef;
  @ViewChild('dataTables_info', { static: false }) info?: ElementRef;

  ngOnInit() {
    this.comunicatorSvc.getData().pipe(tap(res => this.result = res))
      .subscribe();
    this.tableSearch = new FormGroup({
      search: new FormControl(),
      numberRows: new FormControl('10')
    });

  }
  //Se insertan las paginas al DOM
  ngAfterViewInit() {
    this.createPages();
  }

  deleteChildren() {
    let nodes = this.pagination?.nativeElement.childNodes;
    if (nodes.length > 2) {
      for (let index = 1; index < nodes.length - 1;) {
        this.renderer.removeChild(this.pagination?.nativeElement,
          this.pagination?.nativeElement.children[1])
      }
    }
  }

  AddDeleteStyles(classOne: string, classTwo: string) {
    this.renderer.removeClass(this.currentPage, classOne)
    this.renderer.addClass(this.currentPage, classTwo)
  }
  changePage(child: any) {
    this.AddDeleteStyles('selected', 'items')
    this.currentPage = child;
    this.AddDeleteStyles('items', 'selected')
    this.getInfo();
  }

  createPages() {

    this.deleteChildren(); //elimino todas las paginas actuales
    this.numberPages = Math.trunc((this.result.length - 1) /
      this.tableSearch.get('numberRows')?.value) + 1

    //itero sobre el numero de paginas a crear
    for (let i = 0; i < this.numberPages; i++) {
      const child = this.renderer.createElement('li')//creo la pagina
      const value = this.renderer.createText((i + 1).toString())
      this.renderer.setProperty(child, 'value', i)
      this.renderer.listen(child, 'click', (event) => {
        this.changePage(child);
      })
      if (i === 0) {
        this.renderer.addClass(child, 'selected')
        this.currentPage = child;
      } else {
        this.renderer.addClass(child, 'items')

      }
      this.renderer.addClass(child, 'dataTables_info')//
      this.renderer.addClass(child, 'page-item')//a√±ado las clases alos elementos
      this.renderer.addClass(child, 'active')//
      this.renderer.appendChild(child, value)//le agrego el texto al elemento
      this.renderer.insertBefore(this.pagination?.nativeElement,
        child, this.next?.nativeElement)//inserto el elento en la vista

    this.getInfo();
      }

  }

  getInfo() {
    let msj: string = 'mostrando ';
    let nodes = this.info?.nativeElement.childNodes;
    let inicio = Number(this.currentPage.value) *
      Number(this.tableSearch.get('numberRows')?.value) + 1
    let fin = inicio +
      Number(this.tableSearch.get('numberRows')?.value) - 1
    for (let index = 0; index < nodes.length;) {
      this.renderer.removeChild(this.info?.nativeElement,
        this.info?.nativeElement.children[0])
    }
    if (this.result.length<fin) {
      if (this.tableSearch.get("search")?.value !== '' &&
      this.tableSearch.get("search")?.value !== null) {
        msj = msj.concat('del ', String(inicio),
          ' al ', String(this.result.length)," de ",
          String(this.result.length)," entradas ",
          " (filtradas de ", String(this.data.length), " en total)")
      } else {
        msj = msj.concat('del ', String(inicio),
          ' al ', String(this.data.length)," de ",
           String(this.data.length)," entradas")
      }

    } else {
      if (this.tableSearch.get("search")?.value !== ''
      && this.tableSearch.get("search")?.value !== null) {
        msj = msj.concat('del ', String(inicio),
          ' al ', String(fin), ' de ',
          String(this.result.length)," entradas",
          " (filtradas de ", String(this.data.length), " en total)")
      } else {
        msj = msj.concat('del ', String(inicio),
          ' al ', String(fin), ' de ',
          String(this.data.length)," entradas")
      }
    }
    const child = this.renderer.createElement('p')
    const text = this.renderer.createText(msj)
    this.renderer.appendChild(child, text)
    this.renderer.addClass(child, 'dataTables_info')
    this.renderer.appendChild(this.info?.nativeElement, child)
  }

  getValuesToPipe(): any {
    let argsPipe = {
      search: this.tableSearch.get('search')?.value,
      indexCurrentPage: (this.currentPage === undefined) ?
        0 : Number(this.currentPage.value),
      numberRows: Number(this.tableSearch.get('numberRows')?.value)
    }
    return argsPipe;
  }
  nextPage() {
    let nodes = this.pagination?.nativeElement.children;
    this.changePage(nodes[Number(this.currentPage.value) + 2]);
  }
  previousPage() {
    let nodes = this.pagination?.nativeElement.children;
    this.changePage(nodes[Number(this.currentPage.value)]);
  }
}
