import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nuevo-producto',
  templateUrl: './nuevo-producto.component.html',
  styleUrls: ['./nuevo-producto.component.css']
})
export class NuevoProductoComponent extends DinamicInput {
  formNewProduct!: FormGroup;
  constructor(private fb: FormBuilder,private routes:Router,private comunicatorSvc:ComunicatorComponetsService) {
    super();
  }
  ngOnInit() {

    this.formNewProduct = this.fb.group(
      {
        barcode: new FormControl(''),
        productname: new FormControl(''),
        mark: new FormControl(''),
        model: new FormControl(''),
        discount: new FormControl(''),
        timeUnit:new FormControl(''),
      }
    )
  }
  ngAfterViewInit(){
    let title=[];
    /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
    let ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
    ruta=ruta.replaceAll('%20',' ').toUpperCase();
    title.push(ruta);//añado el titulo al array
    title.push("fas fa-box fa-fw");//aañado el icono del titulo al array
    this.comunicatorSvc.setTitleComponent(title);
  }

}
