import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-nueva-caja',
  templateUrl: './nueva-caja.component.html',
  styleUrls: ['./nueva-caja.component.css']
})

export class NuevaCajaComponent {
  focus: Array<boolean> = new Array(2);
  positionLabel: Array<boolean> = new Array(2);
  nuevaCaja!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.nuevaCaja = this.fb.group({
      entradaNumeroCaja: new FormControl('')
    })
  }
  isFocused(index: number,label:string) {
    this.focus[index] = !this.focus[index];
    if (this.nuevaCaja.get(label)?.value) {
      this.positionLabel[index]=false;
    }else {
      this.positionLabel[index]=true;
    }
  }

  animationLabel(label:string) {
    let styles!: object;
    if (this.nuevaCaja.get(label)?.value) {
      styles = {
        "top": "0",
        "font-size": "1rem",
      }
    } 
    return styles;
  }
}
