import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';
import { DinamicTable } from 'src/app/utils/DinamicTable';

@Component({
  selector: 'app-nueva-venta',
  templateUrl: './nueva-venta.component.html',
  styleUrls: ['./nueva-venta.component.css']
})
export class NuevaVentaComponent extends DinamicInput{

  formNewSell!: FormGroup;
  constructor(private fb: FormBuilder, private routes: Router, private comunicatorSvc: ComunicatorComponetsService) {
    super();
  }
  ngOnInit() {
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//a単ado el titulo al array
      title.push("fas fa-cart-plus fa-fw");//aa単ado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
    this.formNewSell = this.fb.group(
      {
        barcode: new FormControl(''),
        productQuantity: new FormControl(''),
        date: new FormControl(''),

        time: new FormControl(''),
      }
    )
  }

}
