import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nueva-caja',
  templateUrl: './nueva-caja.component.html',
  styleUrls: ['./nueva-caja.component.css']
})

export class NuevaCajaComponent extends DinamicInput {

  newRegister!: FormGroup;
  usedClass: string[] = [];
  title: string[];
  registerToUpdate: string[] = []
  constructor(private fb: FormBuilder, private comunicatorSvc$: ComunicatorComponetsService,
    private routes: Router, private route: ActivatedRoute) {
    super();
    this.usedClass[0] = "input-on-focus";
    let ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
    this.title = []
    this.title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
    if (ruta.indexOf("actualizar")) {
      this.title.push("fa-solid fa-cash-register fa-fw");//aañado el icono del titulo al array
    } else {
      this.title.push("fa fa-arrows-rotate");
    }
    this.comunicatorSvc$.setTitleComponent(this.title);
  }

  ngOnInit() {

    this.newRegister = this.fb.group({
      name: new FormControl('', [Validators.required, Validators.pattern(/^[a-zA-Z0-9 ]+$/)]),
      state: new FormControl('Habilitada')
    })
    this.route.paramMap.subscribe((params: ParamMap) => {
      //let id = +params.get('id_register');
      console.log("this id register"+params.get('id_register'))
    });

    /* this.comunicatorSvc$.getTargetToUpdate().subscribe(
      {
        next: res => {
          let other:any=res
          this.newRegister.get('name')?.setValue(other.name)
          this.newRegister.get('state')?.setValue((other.state===undefined)?'Habilitada':other.state)
        },

      }
    ) */

  }

  submit() {
    if (this.newRegister.valid) {
      this.comunicatorSvc$.setRegister(this.newRegister.value).pipe(
        tap(res => console.log(res))
      ).subscribe()
      console.log(this.newRegister.value)
    }
  }
  clean() {
    this.newRegister.get('name')?.setValue('');
  }
}
