import { Component, Renderer2 } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ComprasService } from 'src/app/content/compras/service/compras.service';
import { VentasService } from 'src/app/content/ventas/service/ventas.service';
import { buy, buy_product, coin, kardex, sell, sell_product } from 'src/app/interfaces/interfaces';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicTable } from 'src/app/utils/DinamicTable';
import { Location } from '@angular/common';
import { CoinsService } from 'src/app/content/configuraciones/service/coins.service';
import { KardexService } from '../../service/kardex.service';

@Component({
  selector: 'app-detalles-kardex',
  templateUrl: './detalles-kardex.component.html',
  styleUrls: ['./detalles-kardex.component.css']
})
export class DetallesKardexComponent extends DinamicTable {
  buy!: buy
  buyProduct!: buy_product
  sell!: sell
  sellProduct!: sell_product
  ruta!: string
  title: string[] = []
  kardex!:kardex
  mainCoin!: coin
  constructor(private routes: Router,
    public comunicatorSvc: ComunicatorComponetsService,
    public override renderer: Renderer2,
    private route: ActivatedRoute,
    private location: Location,
    private comprasSvc: ComprasService,
    private coinsSvc:CoinsService,
    private kardexSvc:KardexService) {
    super(renderer)
  }
  ngOnInit() {
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2, this.routes.url.lastIndexOf('?'));
      this.title.push("fas fa-coins fa-fw");//aa単ado el icono del titulo al array
      this.title.push(decodeURI(this.ruta).toUpperCase());//a単ado el titulo al array
      this.comunicatorSvc.setTitleComponent(this.title);
    });
    this.coinsSvc.getMainCoin().subscribe(res => {
      if (res instanceof Array)
        this.mainCoin = res[0]
    })
    this.route.queryParams.subscribe((params) => {
      this.comprasSvc.getBuy(params['id']).subscribe(res => {
        if (res instanceof Array)
          this.buy = res[0]
      })
      this.comprasSvc.getProductBuy(params['id'], params['id_product']).subscribe(res => {
        if (res instanceof Array)
          this.buyProduct = res[0]
      })
      this.kardexSvc.getKardexById('compra',params['id']).subscribe(res=>{
        if(res instanceof Array){
          console.log(res)
          this.kardex=res[0]
        }
      })
    })

  }
  redirectToKardex() {
    this.location.back()
  }
}
