import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-datos-de-la-empresa',
  templateUrl: './datos-de-la-empresa.component.html',
  styleUrls: ['./datos-de-la-empresa.component.css']
})
export class DatosDeLaEmpresaComponent extends DinamicInput{
  formBuilding!: FormGroup;

  constructor(private fb: FormBuilder) {
    super();
 }

  ngOnInit() {
    this.formBuilding = this.fb.group({
      documentNumber: new FormControl('', [Validators.required, Validators.pattern(/^[0-9]+$/)]),
      name: new FormControl('', [Validators.required, Validators.pattern(/^[a-zA-Z ]+$/)]),
      address: new FormControl('', [Validators.pattern(/^[a-zA-Z ]+$/)]),
      phoneNumber: new FormControl('', [Validators.pattern(/^[0-9]+$/)]),
      email: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      nameTax: new FormControl('',[Validators.required,Validators.pattern(/^[a-zA-Z ]+$/)]),
      taxRate: new FormControl('', [Validators.required,Validators.pattern(/^[0-9]+$/)]),

    })
  }
  submit() {
    if (this.formBuilding.valid)
      console.log("registrd")
  }
  clean() {
    this.formBuilding.get('documentNumber')?.setValue('');
    this.formBuilding.get('name')?.setValue('');
    this.formBuilding.get('address')?.setValue('');
    this.formBuilding.get('phoneNumber')?.setValue('');
    this.formBuilding.get('email')?.setValue('');
    this.formBuilding.get('nameTax')?.setValue('');
    this.formBuilding.get('taxRate')?.setValue('');
  }
}
