import { Component, Renderer2 } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ComprasService } from 'src/app/content/compras/service/compras.service';
import { VentasService } from 'src/app/content/ventas/service/ventas.service';
import { buy, buy_product, coin, kardex, sell, sell_product } from 'src/app/interfaces/interfaces';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicTable } from 'src/app/utils/DinamicTable';
import { Location } from '@angular/common';
import { CoinsService } from 'src/app/content/configuraciones/service/coins.service';
import { KardexService } from '../../service/kardex.service';

@Component({
  selector: 'app-detalles-kardex',
  templateUrl: './detalles-kardex.component.html',
  styleUrls: ['./detalles-kardex.component.css']
})
export class DetallesKardexComponent extends DinamicTable {
  buy!: buy
  buyProduct!: buy_product
  sell!: sell
  sellProduct!: sell_product
  ruta!: string
  title: string[] = []
  kardex!:kardex
  mainCoin!: coin
  constructor(private routes: Router,
    public comunicatorSvc: ComunicatorComponetsService,
    public override renderer: Renderer2,
    private route: ActivatedRoute,
    private location: Location,
    private comprasSvc: ComprasService,
    private coinsSvc:CoinsService,
    private kardexSvc:KardexService) {
    super(renderer)
  }
  ngOnInit() {
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      this.ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2, this.routes.url.lastIndexOf('?'));
      this.title.push("fas fa-coins fa-fw");//aañado el icono del titulo al array
      this.title.push(decodeURI(this.ruta).toUpperCase());//añado el titulo al array
      this.comunicatorSvc.setTitleComponent(this.title);
    });
    this.coinsSvc.getMainCoin().subscribe(res => {
      if (res instanceof Array)
        this.mainCoin = res[0]
    })
    this.route.queryParams.subscribe((params) => {
      this.comprasSvc.getBuy(params['id']).subscribe(res => {
        if (res instanceof Array)
          this.buy = res[0]
      })
      this.comprasSvc.getProductBuy(params['id'], params['id_product']).subscribe(res => {
        if (res instanceof Array)
          this.buyProduct = res[0]
      })
      this.kardexSvc.getKardexById('compra',params['id']).subscribe(res=>{
        if(res instanceof Array){
          console.log(res)
          this.kardex=res[0]
        }
      })
    })

  }
  redirectToKardex() {
    this.location.back()
  }
}
<div class="form-container">
    <div class="back">
        <button (click)="redirectToKardex()" class="form-button btn-save"><i class="fas fa-reply"></i>
            &nbsp;Regresar</button>
    </div>
    <h3 class="form-title">
        &nbsp;Datos de la venta
    </h3>
    <div class="datos" *ngIf="buy!==undefined">
        <div>
            <h3>ENTRADAS</h3>
            <p><strong>Fecha: </strong>{{kardex.date|date:'dd/MM/yyyy'}} {{kardex.time|date:'hh:mm:ss aaaa'}}</p>
            <p><strong>N° factura: </strong>{{kardex.id_operation}}</p>
            <p><strong>Entrada de unidades: </strong>{{kardex.quantity_products}}</p>
            <p><strong>Costo de unidades: </strong>{{buyProduct.buy_price|currency:kardex.coin_kardex+' '}}</p>
            <p><strong>Precio de venta de unidades: </strong>{{buyProduct.sell_price|currency:kardex.coin_kardex+' '}}</p>
        </div>
       <!--  <div>
            <h3>CAJA Y USUARIO</h3>
            <p><strong>Caja: </strong>{{register_buy.name}}</p>
            <p><strong>Vendedor: </strong>{{user_buy.names_user}} {{user_buy.last_names_user}}</p>
            <p><strong>Cliente: </strong> {{client.names_client}} {{client.last_names_client}}</p>
        </div> -->
        <div>
            <h3>EXISTENCIAS</h3>
            <p><strong>Inventario inicial: </strong>{{kardex.exist-kardex.quantity_products}}</p>
            <p><strong>Precio inicial del producto: </strong>{{kardex.price|currency:kardex.coin_kardex+' '}}
                <i *ngIf="kardex.coin_kardex!=mainCoin.symbol"> /
                    {{comunicatorSvc.converterToMainCoin(kardex.price,kardex.exchange)|currency:mainCoin.symbol+' '}}</i></p>
            <p><strong>Inventario final del producto:</strong> {{kardex.exist}}</p>
            <p><strong>Precio final del producto :</strong> 
                {{comunicatorSvc.converterToSecondaryCoin(kardex.weighted_averages_sell,kardex.exchange)|currency:kardex.coin_kardex+' '}}
                <i *ngIf="kardex.coin_kardex!=mainCoin.symbol"> /
                    {{kardex.weighted_averages_sell}}</i></p>
            <p><strong>Total: </strong> {{kardex.exist}}</p>
        </div> 

    </div>
</div>
