import { Component } from '@angular/core';
import { ComunicatorComponetsService } from '../services/comunicator-componets.service';
import { tap } from 'rxjs';
@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})



export class navBar {
  subMenuAdministracion = false;
  subMenuProductos = false;
  subMenuCompras = false;
  subMenuVentas = false;
  subMenuDevoluciones = false;
  subMenuKardex = false;
  subMenuReportes = false;
  subMenuConfiguraciones = false;
  show!: boolean;
  info: String[]=[];
  constructor(private comunicatorSvc: ComunicatorComponetsService) { }

  ngOnInit() {
    this.comunicatorSvc.getShowHideNavBar()
      .pipe(tap(res => this.show = res))
      .subscribe();
  }

  public onClickOption(value: String) {
    this.info.length=0;
    this.info.push(value);
    switch (value) {
      case 'DASHBOARD':
        this.info.push("fa-dashcube",
        "¡Bienvenida Leomarys Riera! este es el panel principal del sistema "+
        "acá podrá encontrar atajos para acceder a los distintos listados de "+
        "cada módulo del sistema");
        console.log(this.info);
        break;
    }
    this.comunicatorSvc.setItemSelected(this.info);
  }

  changeStyle(active: boolean) {
    const styles = {
      "background-color": active ? "blue" : "transparent",
    }
    return styles;
  }
  arrowStyle(active: boolean) {
    const styles = {
      "transform": active ? "rotate(180deg)" : "none"
    }
    return styles;
  }
}
