import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nuevo-proveedor',
  templateUrl: './nuevo-proveedor.component.html',
  styleUrls: ['./nuevo-proveedor.component.css']
})
export class NuevoProveedorComponent extends DinamicInput{

  formNewProvider!: FormGroup;

  constructor(private fb: FormBuilder) {
    super();
   }

  ngOnInit() {
    this.formNewProvider = this.fb.group({
      documentNumber: new FormControl('',[Validators.required,Validators.pattern(/^[0-9]+$/)]),
      nameProvider: new FormControl('',[Validators.required,Validators.pattern(/^[a-zA-Z ]+$/)]),
      addressProvider: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      nameBoss: new FormControl('',[Validators.pattern(/^[a-zA-Z ]+$/)]),
      phoneNumber: new FormControl('',[Validators.pattern(/^[0-9]+$/)]),
      email: new FormControl('',[Validators.email]),
    })
  }
  submit() {
    if(this.formNewProvider.valid)
    console.log("registrd")
  }
  clean(){
    this.formNewProvider.get('documentNumber')?.setValue('');
    this.formNewProvider.get('nameProvider')?.setValue('');
    this.formNewProvider.get('addressProvider')?.setValue('');
    this.formNewProvider.get('nameBoss')?.setValue('');
    this.formNewProvider.get('phoneNumber')?.setValue('');
    this.formNewProvider.get('email')?.setValue('');
  }
}
