import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-nuevo-usuario',
  templateUrl: './nuevo-usuario.component.html',
  styleUrls: ['./nuevo-usuario.component.css']
})
export class NuevoUsuarioComponent extends DinamicInput{
  formNewUser!: FormGroup;

  constructor(private fb: FormBuilder,private routes:Router,
    private comunicatorSvc:ComunicatorComponetsService) {
    super();
 }

  ngOnInit() {
    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//a単ado el titulo al array
      title.push((ruta.indexOf("nuevo"))?"fas fa-user-cog fa-fw":"fa fa-user-tie fa-fw");//aa単ado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    });
    this.formNewUser = this.fb.group({
      documentNumber: new FormControl(''),
      namesUser: new FormControl(''),
      lastNamesUser: new FormControl(''),
      nameBoss: new FormControl(''),
      phoneNumber: new FormControl(''),
      email: new FormControl(''),
      password: new FormControl(''),
      repeatPassword: new FormControl(''),
    })
  }
}
