import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicInput } from 'src/app/utils/DinamicInput';

@Component({
  selector: 'app-buscar-compras',
  templateUrl: './buscar-compras.component.html',
  styleUrls: ['./buscar-compras.component.css']
})
export class BuscarComprasComponent extends DinamicInput{
  data$?:Subscription;
  constructor(private fb: FormBuilder,

    private comunicatorSvc: ComunicatorComponetsService, private routes: Router) {
    super();
  }
  ngOnInit() {

    let title: string[] = [];
    let ruta;
    setTimeout(() => {
      /* Obtengo la ruta actual y la transformo para obtener el titulo del componente*/
      ruta = this.routes.url.slice((this.routes.url.slice(1).indexOf('/')) + 2);
      title.push(decodeURI(ruta).toUpperCase());//añado el titulo al array
      title.push("fas fa-search-dollar fa-fw");//aañado el icono del titulo al array
      this.comunicatorSvc.setTitleComponent(title);
    })
  }
  //Se insertan las paginas al DOM

  ngOnDestroy(){
    this.data$?.unsubscribe();
  }
}
<div class="form-container">
    <h3 class="form-title">
        &nbsp;Buscar por fecha
    </h3>
    <div class="form" [formGroup]="formSearchBuys">
        <div class="form-group">
            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[0]}">
                <label class="form-label" [ngClass]="{'label-on-focus-down':
                elementHasFocus[0]&&positionLabel[0],
                'label-on-focus-up':
                elementHasFocus[0]&&!positionLabel[0]}" [ngStyle]="animationLabel(formSearchBuys,
                'initialDate')">
                    Fecha de inicio
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input type= "date" formControlName="initialDate" (focus)="isFocused(0,formSearchBuys,'initialDate')"
                    (focusout)="isFocused(0,formSearchBuys,'initialDate')" class="form-input">
            </div>

            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[1]}">
                <label class="form-label" [ngClass]="{'label-on-focus-down':
            elementHasFocus[1]&&positionLabel[1],
            'label-on-focus-up':
            elementHasFocus[1]&&!positionLabel[1]}" [ngStyle]="animationLabel(formSearchBuys,
            'endDate')">
                    Fecha de fin
                    <i class="fab fa-font-awesome-alt"></i>
                </label>
                <input formControlName="endDate" (focus)="isFocused(1,formSearchBuys,'endDate')"
                    (focusout)="isFocused(1,formSearchBuys,'endDate')" class="form-input">
            </div>
        </div>
        <h3 class="form-title">
            <i class="fas fa-calendar-alt"></i>
            &nbsp;Buscar por ticket, proveedor o usuario
        </h3>
        <div class="form-group">
            <div class="form-control" [ngClass]="{'input-on-focus':elementHasFocus[2]}">
                <!-- <label class="form-label" [ngClass]="{'label-on-focus-down':
                elementHasFocus[2]&&positionLabel[2],
                'label-on-focus-up':
                elementHasFocus[2]&&!positionLabel[2]}" [ngStyle]="animationLabel(formSearchBuys,
                'campSearch')">
                    Buscar comp
                    <i class="fab fa-font-awesome-alt"></i>
                </label> -->
                <input formControlName="campSearch" (focus)="isFocused(2,formSearchBuys,'campSearch')"
                    (focusout)="isFocused(2,formSearchBuys,'campSearch')" class="form-input">
            </div>
        </div>
        <div class="form-actions">
            <button class="form-button btn-save">
                <i class="far fa-save"></i>
                &nbsp;&nbsp;ACEPTAR</button>
        </div>
    </div>
</div>
