import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { DinamicInput } from 'src/app/utils/util';

@Component({
  selector: 'app-nueva-categoria',
  templateUrl: './nueva-categoria.component.html',
  styleUrls: ['./nueva-categoria.component.css']
})
export class NuevaCategoriaComponent extends DinamicInput {

  formNewCategory!: FormGroup;

  constructor(private fb: FormBuilder) {
    super();
   }

  ngOnInit() {
    this.formNewCategory = this.fb.group({
      nameCategory: new FormControl(''),
      positionCategory: new FormControl('')
    })
  }
}
