import { Pipe, PipeTransform } from '@angular/core';
import { ListaCajasComponent } from 'src/app/content/administracion/cajas/lista-cajas/lista-cajas.component';

@Pipe({
  name: 'tableFilter'
})
export class TableFilterPipe implements PipeTransform {
  private result:any = [];
  transform(value: any, args: any): any {
    this.result=[];
    for (let newValue of value) {
      if (args.search === null || args.search === '') {
        this.result.push(newValue)
      } else if (newValue.name.indexOf(args.search) > -1) {
        this.result.push(newValue)
      }
      console.log("pase filtro")
    }
    const inicio: number = args.indexCurrentPage * args.numberRows;
    const fin: number = inicio + args.numberRows;
    return this.result.slice(inicio, fin);
  }
}
