import { DOCUMENT } from '@angular/common';
import { Component, Inject } from '@angular/core';
import { Subscription } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';
import { DinamicComponent } from 'src/app/utils/DinamicComponent';

@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css']
})
export class ClientesComponent extends DinamicComponent {
  title: string[] = [];
  data$?: Subscription;
  constructor(@Inject(DOCUMENT) document: any,private comunicatorSvc: ComunicatorComponetsService) {
    super(String(document.location.href).includes('nuevo'),
    String(document.location.href).includes('lista'))
  } 
  ngOnInit() {
    this.data$= this.comunicatorSvc.getTitleComponent().subscribe(titleComponet => {
       this.title = titleComponet
       switch (titleComponet[1]) {
         case "NUEVO CLIENTE":
           this.option1 = true
           this.option2 = false
           break;
         case "LISTA CLIENTES":
           this.option1 = false
           this.option2 = true
           break;
         case "ACTUALIZAR CLIENTE":
           this.option1 = false
           this.option2 = false
           break;
       }
     })
   }
   ngOnDestroy() {
     this.data$?.unsubscribe();
   }
}
<div class="scroll info-container container">
    <h2 class="title">
        <i class="{{title[0]}}">
        </i> &nbsp;{{title[1]}}
    </h2>
    <p class="msj">En el módulo CLIENTE podrá registrar en el 
        sistema los datos de sus clientes mas frecuentes, para 
        realizar ventas, además podra realizar busquedas de 
        los mismos, actualizar sus datos o eliminarlos 
        si asi lo desea
    </p>
    <ul class="list-options">
        <li (click)="selected(true,false)" 
        [ngClass]="selectedClass(option1)">
            <a routerLink="nuevo cliente" 
            [class.active]="option1" class="option-component">
                <div class="container">
                    <i class="fa fa-child fa-fw"></i>
                    &nbsp;&nbsp;NUEVO CLIENTE
                </div>
            </a>

        </li>
        <li (click)="selected(false,true)" 
        [ngClass]="selectedClass(option2)">
            <a routerLink="lista clientes" 
            [class.active]="option2" class="option-component">
                <div class="container">
                    <i class="fas fa-clipboard-list fa-fw"></i>
                    &nbsp;&nbsp;LISTA DE CLIENTES
                </div>
            </a>
        </li>

    </ul>
    <router-outlet></router-outlet>
</div>
