import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { tap } from 'rxjs';
import { ComunicatorComponetsService } from 'src/app/services/comunicator/comunicator-componets.service';

@Component({
  selector: 'app-compras',
  templateUrl: './compras.component.html',
  styleUrls: ['./compras.component.css']
})
export class ComprasComponent {

  title:string[]=[];
  constructor(private comunicatorSvc:ComunicatorComponetsService,private routes:Router) {}
  ngOnInit(){

    this.comunicatorSvc.getTitleComponent().pipe(tap(res=>this.title=res)).subscribe();
  }
}
<div class="scroll info-container container">
    <h2 class="title">
        <i class={{title[1]}}></i>
        &nbsp;{{title[0]}}
    </h2>
    <p class="msj">En el módulo COMPRAS usted podrá registrar compras de productos ya sean nuevos o ya 
        registrados en el sistema.Tambien puede ver la lista de todas las compras realizadas, buscar 
        compras y ver información mas detallada de cada compra
    </p>

    <router-outlet></router-outlet>      
</div>
